import React, { useState } from 'react';

const IndiaMap = () => {
  const [hoveredState, setHoveredState] = useState('');

  const handleStateHover = (stateName) => {
    setHoveredState(stateName);
  };

  const statesData = [
    { name: 'State 1', path: 'M100 100 L200 100 L200 200 L100 200 Z' },
    { name: 'State 2', path: 'M300 100 L400 100 L400 200 L300 200 Z' },
    { name: 'State 3', path: 'M500 100 L600 100 L600 200 L500 200 Z' },
  ];

  return (
    <svg
      xmlns="http://www.w3.org/2000/svg"
      viewBox="0 0 960 600"
      width="100%"
      height="auto"
    >
      {/* Render the map with paths for each state */}
      {statesData.map((state) => (
        <path
          key={state.name}
          d={state.path}
          fill="#e0e0e0"
          stroke="#9e9e9e"
          strokeWidth="0.5"
          onMouseEnter={() => handleStateHover(state.name)}
          onMouseLeave={() => setHoveredState('')}
        />
      ))}

      {/* Render the tooltip for the hovered state */}
      {hoveredState && (
        <text x="20" y="20" fill="#000">
          Hovered State: {hoveredState}
        </text>
      )}
    </svg>
  );
};

export default IndiaMap;
