        sectors: ["Agriculture", "Manufacturing", "IT"],
      },
      {
        name: "Rajasthan",
        sectors: ["Agriculture", "Tourism", "IT"],
      },
      {
        name: "Sikkim",
        sectors: ["Tourism", "Agriculture", "IT"],
      },
      {
        name: "Telangana",
        sectors: ["Agriculture", "Manufacturing", "IT"],
      },
      {
        name: "Tripura",
        sectors: ["Agriculture", "Tourism", "IT"],
      },
      {
        name: "Uttar Pradesh",
        sectors: ["Agriculture", "Manufacturing", "IT"],
      },
      {
        name: "Uttarakhand",
        sectors: ["Agriculture", "Tourism", "IT"],
      },
      {
        name: "West Bengal",
        sectors: ["Agriculture", "Manufacturing", "IT"],
      },
    ],
  };
  
const IndiaMap = () => {
  const map = L.map('map').setView([20, 78], 4);

  L.India.map(map);

  // Add hovering attributes to the states.
  const states = L.layerGroup();
  for (const state of India.states) {
    const s = L.polygon(state.coords).addTo(states);
    s.bindTooltip(state.name + ': ' + state.sectors.join(', '));
  }

  map.addLayer(states);

  return (
    <div id="map"></div>
  );
};

export default IndiaMap;
