import React from "react";

import { Box, Container, Stack, Typography } from "@mui/material";
import BarChart from "../charts/BarChart";
import StateSocieties from "../charts/StateSocieties";
import { PieChart } from "../charts/PieChart";

import t1 from '../images/smart-city.png'
import t2 from '../images/smart-city.png'
function Tile({ title, content, img }) {
  return (
    <Box
      sx={{
        boxShadow: 5,
        width: "fit-content",
        padding: 6,
        backgroundColor: "white",
        borderRadius: 2,
      }}
    >
      <Stack direction={"row"} gap={5} alignItems="center">
        <Stack direction={"column"}>
          <Typography variant="h6">{title}</Typography>
          <Typography variant="h4">{content}</Typography>
        </Stack>
        <img src={img}  width={'50px'} height={'50px'} />
      </Stack>
    </Box>
  );
}

const Dashboard = () => {
  const tiles = [
    {
      title: "Total Registered Societies",
      content: "100,000",
      img: `${t1}`
        },
    {
      title: "Total Sectors",
      content: "100,000",
      img: `${t1}`
    },
    {
      title: "Total Revenue",
      content: "100,000",
      img:`${t1}`
    },
  ];

  return (
    <>
      <Container
        disableGutters
        maxWidth={"xl"}
        sx={{
          backgroundColor: "#e9e9e9",
          padding: 2,
          minHeight: "100vh",
          display: "flex",
          flexDirection: "column",
          alignItems: "center",
          justifyContent: "center",
        }}
      >
        <Typography variant={"h4"} sx={{ textAlign: "center", mb: 4 }}>
          Dashboard
        </Typography>
        <Stack direction={"column"} gap={4} alignItems="center">
          <Stack
            direction={"row"}
            gap={5}
            sx={{
              width: "100%",
              justifyContent: "space-evenly",
            }}
          >
            {tiles.map((item, idx) => {
              return (
                <Tile key={idx} title={item.title}  img={item.img} content={item.content} />
              );
            })}
          </Stack>

          <Stack
            direction={"column"}
            gap={3}
            sx={{
              width: "100%",
              alignItems: "center",
            }}
          >
            <Box
              width={"90%"}
              sx={{
                backgroundColor: "white",
                borderRadius: 2,
                padding: 3,
              }}
            >
              <StateSocieties />
            </Box>

            <Stack direction={"row"} gap={3} width={"100%"} justifyContent="center">
              <Box
                width={"45%"}
                sx={{
                  backgroundColor: "white",
                  borderRadius: 2,
                  padding: 3,
                }}
              >
                <BarChart />
              </Box>

              <Box
                width={"45%"}
                sx={{
                  backgroundColor: "white",
                  borderRadius: 2,
                  padding: 3,
                }}
              >
                <PieChart />
              </Box>
            </Stack>
          </Stack>
        </Stack>
      </Container>
    </>
  );
};

export default Dashboard;
