import React from "react";
import { Box, Container, Grid, Stack, Typography } from "@mui/material";
import BarChart from "../charts/BarChart";
import StateSocieties from "../charts/StateSocieties";
import { PieChart } from "../charts/PieChart";

import t1 from '../images/smart-city.png'
import t2 from '../images/factory.png'

function Tile({ title, content, img }) {
  return (
    <Box
      sx={{
        boxShadow: 5,
        width: "fit-content",
        padding: 6,
        backgroundColor: "white",
        borderRadius: 2,
      }}
    >
      <Stack direction={"row"} gap={5} alignItems="center">
        <Stack direction={"column"}>
          <Typography variant="h6">{title}</Typography>
          <Typography variant="h4">{content}</Typography>
        </Stack>
        <img src={img}  width={'50px'} height={'50px'} />
      </Stack>
    </Box>
  );
}

const Dashboard = () => {
  const tiles = [
    {
      title: "Total Registered Societies",
      content: "100,000",
      img: `${t1}`
        },
    {
      title: "Total Sectors",
      content: "100,000",
      img: `${t2}`
    },
    {
      title: "Total Revenue",
      content: "100,000",
      img:`${t1}`
    },
  ];

  return (
    <Container
      disableGutters
      maxWidth="xl"
      sx={{
        backgroundColor: "#f3f7f9",
        minHeight: "100vh",
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
        justifyContent: "center",
        padding: "24px",
      }}
    >
      <Typography variant="h4" sx={{ textAlign: "center", mb: 4, color: "#354052" }}>
        Dashboard
      </Typography>
      <Grid container spacing={2} justifyContent="center">
        {tiles.map((item, idx) => (
          <Grid item key={idx} xs={12} sm={6} md={3}>
            <Tile title={item.title} content={item.content} />
          </Grid>
        ))}
      </Grid>
      <Stack direction="row" gap={3} width="100%" justifyContent="center" mt={4}>
        <Box width="50%" sx={{ backgroundColor: "#ffffff", borderRadius: 8, padding: 3 }}>
          <BarChart />
        </Box>
        <Box width="50%" sx={{ backgroundColor: "#ffffff", borderRadius: 8, padding: 3 }}>
          <PieChart />
        </Box>
      </Stack>
      <Box width="100%" mt={4} sx={{ backgroundColor: "#ffffff", borderRadius: 8, padding: 3 }}>
        <StateSocieties />
      </Box>
    </Container>
  );
};

export default Dashboard;
