import React, { useState } from 'react';
const WebDesign = () => {
  const [formData, setFormData] = useState({
    name: '',
    headquarters: '',
    areaOfOperations: '',
    mainObjectives: '',
    necessity: '',
    certificateB: '',
    certificateC: '',
    certificateD: '',
    members: [],
    correspondentName: '',
    correspondentAddress: '',
    bankName: '',
    bankBalance: '',
    viabilityScheme: '',
    authorizedPersons: [],
    applicants: []
  });

  const handleInputChange = (event) => {
    const { name, value } = event.target;
    setFormData((prevFormData) => ({
      ...prevFormData,
      [name]: value
    }));
  };

  const handleMemberChange = (event, index) => {
    const { name, value } = event.target;
    setFormData((prevFormData) => {
      const newMembers = [...prevFormData.members];
      newMembers[index] = { ...newMembers[index], [name]: value };
      return { ...prevFormData, members: newMembers };
    });
  };

  const handleAddMember = () => {
    setFormData((prevFormData) => ({
      ...prevFormData,
      members: [...prevFormData.members, { state: '', count: '' }]
    }));
  };

  const handleRemoveMember = (index) => {
    setFormData((prevFormData) => ({
      ...prevFormData,
      members: prevFormData.members.filter((_, i) => i !== index)
    }));
  };

  const handleApplicantChange = (event, index) => {
    const { name, value } = event.target;
    setFormData((prevFormData) => {
      const newApplicants = [...prevFormData.applicants];
      newApplicants[index] = { ...newApplicants[index], [name]: value };
      return { ...prevFormData, applicants: newApplicants };
    });
  };

  const handleAddApplicant = () => {
    setFormData((prevFormData) => ({
      ...prevFormData,
      applicants: [
        ...prevFormData.applicants,
        {
          name: '',
          institution: '',
          age: '',
          nationality: '',
          profession: '',
          state: '',
          address: '',
          shareCapitalAmount: '',
          societyPosition: ''
        }
      ]
    }));
  };

  const handleRemoveApplicant = (index) => {
    setFormData((prevFormData) => ({
      ...prevFormData,
      applicants: prevFormData.applicants.filter((_, i) => i !== index)
    }));
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    // handle form submission here
    console.log(formData);
  };

  return (
    <form onSubmit={handleSubmit}>
      <h1>Application for registration of a Multi-State Cooperative Society under the Multi-State Cooperative Societies Act, 2002</h1>
      <p>To, The Central Registrar of Cooperative Societies, New Delhi.</p>
      <p>Sir, We submit herewith a proposal for registration of the following Multi-State Cooperative Society along with enclosures as indicated below :</p>
      <p>We also declare that the information given herewith including that in the enclosures is correct to the best of our knowledge.</p>
      <label>
        (a) Name of the proposed Multi-State Co-operative Society:
        <input type="text" name="name" value={formData.name} onChange={handleInputChange} />
      </label>
      <br />
      <label>
        (b) Headquarters and address to be registered:
        <input type="text" name="headquarters" value={formData.headquarters} onChange={handleInputChange} />
      </label>
      <br />
      <label>
        (c) Area of operations:
        <input type="text" name="areaOfOperations" value={formData.areaOfOperations} onChange={handleInputChange} />
      </label>
      <br />
      <label>
        (d) Main objectives:
        <input type="text" name="mainObjectives" value={formData.mainObjectives} onChange={handleInputChange} />
      </label>
      <br />
      <label>
        (e) Why is it absolutely necessary for the society to be registered under the Multi-State Cooperative Societies Act,2002:
        <input type="text" name="necessity" value={formData.necessity} onChange={handleInputChange} />
      </label>
      <br />
      <label>
        (f) A certificate in respect of provisions of clause (b) or sub-section(1) of section 7:
        <input type="text" name="certificateB" value={formData.certificateB} onChange={handleInputChange} />
      </label>
      <br />
      <label>
        (g) A certificate in respect of provisions clause (c) of sub-section (1) of section 7:
        <input type="text" name="certificateC" value={formData.certificateC} onChange={handleInputChange} />
      </label>
      <br />
      <label>
        (h) A certificate in respect of provisions of clause (d) of sub-section (1) of section 7:
        <input type="text" name="certificateD" value={formData.certificateD} onChange={handleInputChange} />
      </label>
      <br />
      <p>(i) If all the members are individuals, indicate the number of persons from each State who have signed the application:</p>
      <table>
        <thead>
          <tr>
            <th>Name of the State</th>
            <th>Number of persons who have Signed the application</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {formData.members.map((member, index) => (
            <tr key={index}>
              <td>
                <input type="text" name="state" value={member.state} onChange={(event) => handleMemberChange(event, index)} />
              </td>
              <td>
                <input type="number" name="count" value={member.count} onChange={(event) => handleMemberChange(event, index)} />
              </td>
              <td>
                <button type="button" onClick={() => handleRemoveMember(index)}>Remove</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
      <button type="button" onClick={handleAddMember}>Add Member</button>
      <br />
      <label>
        (j) Name and address of the applicant for the purpose of further correspondence:
        <input type="text" name="correspondentName" value={formData.correspondentName} onChange={handleInputChange} />
        <input type="text" name="correspondentAddress" value={formData.correspondentAddress} onChange={handleInputChange} />
      </label>
      <br />
      <p>Following documents are enclosed:</p>
      <label>
        (a) A certificate from the _______________ Bank stating credit balance there in favour of the proposed multi-state cooperative society.
        <input type="text" name="bankName" value={formData.bankName} onChange={handleInputChange} />
        <input type="number" name="bankBalance" value={formData.bankBalance} onChange={handleInputChange} />
      </label>
      <br />
      <label>
        (b) A scheme explaining how the proposed multi-state cooperative society has reasonable prospects of becoming a viable unit. We are sending herewith four copies of the bye-laws duly signed.
        <input type="file" name="viabilityScheme" value={formData.viabilityScheme} onChange={handleInputChange} />
      </label>
      <br />
      <p>Following persons are authorised to sign the bye-laws and also to make necessary alteration therein.</p>
      <ul>
        {formData.authorizedPersons.map((person, index) => (
          <li key={index}>
            <input type="text" value={person} onChange={(event) => {
              const newAuthorizedPersons = [...formData.authorizedPersons];
              newAuthorizedPersons[index] = event.target.value;
              setFormData((prevFormData) => ({ ...prevFormData, authorizedPersons: newAuthorizedPersons }));
            }} />
            <button type="button" onClick={() => {
              setFormData((prevFormData) => ({
                ...prevFormData,
                authorizedPersons: prevFormData.authorizedPersons.filter((_, i) => i !== index)
              }));
            }}>Remove</button>
          </li>
        ))}
      </ul>
      <button type="button" onClick={() => {
        setFormData((prevFormData) => ({
          ...prevFormData,
          authorizedPersons: [...prevFormData.authorizedPersons, '']
        }));
      }}>Add Authorized Person</button>
      <br />
      <p>The particulars of the applicants are given here under:</p>
      <table>
        <thead>
          <tr>
            <th>S. No.</th>
            <th>Name</th>
            <th>If representing a corporate body, name of the institution*</th>
            <th>If the applicant is an individual</th>
            <th>Age</th>
            <th>Nationality</th>
            <th>Profession</th>
            <th>Name of the State</th>
            <th>Address</th>
            <th>Amount subscribed to share capital</th>
            <th>In the case of representative of society or a multi-state cooperative society, whether he is Chairpersons/ President or Chief Executive of that society</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {formData.applicants.map((applicant, index) => (
            <tr key={index}>
              <td>{index + 1}</td>
              <td><input type="text" name="name" value={applicant.name} onChange={(event) => handleApplicantChange(event, index)} /></td>
              <td><input type="text" name="institution" value={applicant.institution} onChange={(event) => handleApplicantChange(event, index)} /></td>
              <td></td>
              <td><input type="number" name="age" value={applicant.age} onChange={(event) => handleApplicantChange(event, index)} /></td>
              <td><input type="text" name="nationality" value={applicant.nationality} onChange={(event) => handleApplicantChange(event, index)} /></td>
              <td><input type="text" name="profession" value={applicant.profession} onChange={(event) => handleApplicantChange(event, index)} /></td>
              <td><input type="text" name="state" value={applicant.state} onChange={(event) => handleApplicantChange(event, index)} /></td>
              <td><input type="text" name="address" value={applicant.address} onChange={(event) => handleApplicantChange(event, index)} /></td>
              <td><input type="number" name="shareCapitalAmount" value={applicant.shareCapitalAmount} onChange={(event) => handleApplicantChange(event, index)} /></td>
              <td><input type="text" name="societyPosition" value={applicant.societyPosition} onChange={(event) => handleApplicantChange(event, index)} /></td>
              <td><button type="button" onClick={() => handleRemoveApplicant(index)}>Remove</button></td>
            </tr>
          ))}
        </tbody>
      </table>
      <button type="button" onClick={handleAddApplicant}>Add Applicant</button>

      * If representing a co-operative or any other co-operative body enclose a copy of the Resolution or authorization of the competent authority, authorizing the person to sign the application for registration.

      For Official Use

      Received by registered post on+ _________________ by+ ____________ in the office of the Central Registrar, or by hand from Shri+ _______________ entered in the register of application at S.No. _______________ .

      Signature of officer receiving

      Received registration proposal No. ______________ on ___________ for registration of _______________ Society Ltd., (Proposed) alongwith the enclosures referred to above from ______________ by post/hand deliver.

      Place _____________
      Date _______________
      Central Registrar
      Signature and Stamp

      + blanks to be filled in.

      <input type="submit" value="Submit" />
    </form>
  );
};

export default WebDesign;
